   private void NextStudentButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_NextStudentButtonActionPerformed

      /* Retrieve data from fields, store into object in array, output object's data,
      * and also handle class average and grade curve.
      */
		
      //reset score
      score = 0.0;

      //create student object to hold this student at the right point in the array
      allStudents[StudentsEntered] = new Student();

      //store student's last name
      allStudents[StudentsEntered].setName(LNameField.getText());

      //get student scores and sum them
      score = Double.parseDouble(Grade1Field.getText()) + Double.parseDouble(Grade2Field.getText())
      + Double.parseDouble(Grade3Field.getText());

      //pass score to Student class for averaging and determining grade
      allStudents[StudentsEntered].calcAvg(score, numtests);

      //output data from current student to Student Data section of GUI
		StudentTextBox.append(allStudents[StudentsEntered].print2());
		
		//class average and grade curve
		//track the number of students with each grade
		switch(allStudents[StudentsEntered].getGrade())
		{
			case 'A':
				numAs++;
				break;
			case 'B':
				numBs++;
				break;
			case 'C':
				numCs++;
				break;
			case 'D':
				numDs++;
				break;
			case 'F':
				numFs++;
				break;
			default:
				System.out.println("Something went wrong, your student doesn't have a grade.");
				break;
		}
		
		//track running total of all grades
		runningtotal = runningtotal + allStudents[StudentsEntered].getAvg();
		
		//increment StudentsEntered counter
      StudentsEntered++;
		
		ClassAverageLabel.setText("Average: " + ((float)Math.round(((runningtotal / StudentsEntered) * 100) / 100)));
		AsLabel.setText("As: " + numAs);
		BsLabel.setText("Bs: " + numBs);
		CsLabel.setText("Cs: " + numCs);
		DsLabel.setText("Ds: " + numDs);
		FsLabel.setText("Fs: " + numFs);
   }//GEN-LAST:event_NextStudentButtonActionPerformed

