   private void NumStudentsOKButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_NumStudentsOKButtonActionPerformed
      
		// save number of students from text field to variable for reuse
		numstudents = Integer.parseInt(NumStudentsField.getText());
		
		// create array of students that will hold the entire class
		allStudents = new Student[numstudents];
		
		// lock NumStudentsField to prevent further user input
		NumStudentsField.setEditable(false);
		
   }//GEN-LAST:event_NumStudentsOKButtonActionPerformed

